pub enum Expiration {
    AtHeight(u64),
    AtTime(Timestamp),
    Never {
        },
}

pub enum ParseReplyError {
    SubMsgFailure(String),
    ParseFailure(String),
    BrokenUtf8(std::string::FromUtf8Error),
}

pub enum PaymentError {
    MissingDenom(String),
    ExtraDenom(String),
    MultipleDenoms {
        },
    NoFunds {
        },
    NonPayable {
        },
}

pub enum Threshold {
    AbsoluteCount {
            weight: u64,
        },
    AbsolutePercentage {
            percentage: Decimal,
        },
    ThresholdQuorum {
            threshold: Decimal,
            quorum: Decimal,
        },
}

pub enum ThresholdResponse {
    AbsoluteCount {
            weight: u64,
            total_weight: u64,
        },
    AbsolutePercentage {
            percentage: Decimal,
            total_weight: u64,
        },
    ThresholdQuorum {
            threshold: Decimal,
            quorum: Decimal,
            total_weight: u64,
        },
}

pub enum ThresholdError {
    Std(StdError),
    InvalidThreshold {
        },
    ZeroQuorumThreshold {
        },
    UnreachableQuorumThreshold {
        },
    ZeroWeight {
        },
    UnreachableWeight {
        },
}
